sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_BacklogItemSortRequiredBacklogItems(grBacklogItem, vExpected)
	{
		function TestCase_BacklogItemSortRequiredBacklogItems_CheckResult(vInput,vExpected,vResult)
		{
			//var grBacklogItem = x_snc_devtools.GetRecord('x_snc_agile_backlog_item',vInput.grBacklogItem);
			var grBacklogItem = vInput.grBacklogItem;

			if (x_snc_devtools.IsValidRecord(grBacklogItem) == false)
			{
				return (vExpected == false);
			}

			var aRequiredBacklogItems = x_snc_devtools.GetArrayValue(grBacklogItem.required_backlog_items);

			var nOrder = -1;
			for (var nItem = 0; nItem < aRequiredBacklogItems.length; nItem++)
			{
				var grRequiredBacklogItem = x_snc_devtools.GetRecord(
					'x_snc_agile_backlog_item',
					aRequiredBacklogItems[nItem]);
				if (nOrder >= grRequiredBacklogItem.order)
				{
					return false;
				}
				nOrder = x_snc_devtools.GetIntegerValue(grRequiredBacklogItem.order);
			}

			return true;
		}

		test.TestCase(
			'Function BacklogItemSortRequiredBacklogItems()',
			BacklogItemSortRequiredBacklogItems,
			{
				'grBacklogItem': grBacklogItem
			},
			vExpected,
			TestCase_BacklogItemSortRequiredBacklogItems_CheckResult);
	}

	TestCase_BacklogItemSortRequiredBacklogItems(null,false);

	var grBacklogItemRoot = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItemRoot.initialize();
	grBacklogItemRoot.order = 1;
	var strBacklogItemRootSysId = grBacklogItemRoot.insert();

	TestCase_BacklogItemSortRequiredBacklogItems(
		x_snc_devtools.GetRecord('x_snc_agile_backlog_item',strBacklogItemRootSysId),
		true);

	var grBacklogItemBranchA = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItemBranchA.initialize();
	grBacklogItemBranchA.order = 1000000;
	var strBacklogItemBranchASysId = grBacklogItemBranchA.insert();	

	var grBacklogItemBranchB = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItemBranchB.initialize();
	grBacklogItemBranchB.order = 2000000;
	var strBacklogItemBranchBSysId = grBacklogItemBranchB.insert();		

	grBacklogItemRoot.required_backlog_items = strBacklogItemBranchASysId+','+strBacklogItemBranchBSysId;
	grBacklogItemRoot.update();

	TestCase_BacklogItemSortRequiredBacklogItems(
		x_snc_devtools.GetRecord('x_snc_agile_backlog_item',strBacklogItemRootSysId),
		true);	

	grBacklogItemRoot.required_backlog_items = strBacklogItemBranchBSysId+','+strBacklogItemBranchASysId;
	grBacklogItemRoot.update();

	TestCase_BacklogItemSortRequiredBacklogItems(
		x_snc_devtools.GetRecord('x_snc_agile_backlog_item',strBacklogItemRootSysId),
		true);			
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
