sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetBacklogRecord(vBacklog, vExpected)
	{
		function TestCase_GetBacklogRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vBacklog === null)
			{
				var grBacklog = new GlideRecord('x_snc_agile_backlog');
				grBacklog.query();
				if (grBacklog.getRowCount() == 1)
				{
					return x_snc_devtools.IsValidRecord(vResult);
				}
				return (vResult == false);
			}
			if (vExpected == true)
			{
				return x_snc_devtools.IsValidRecord(vResult);
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetBacklogRecord()',
			GetBacklogRecord,
			{
				'vBacklog': vBacklog
			},
			vExpected,
			TestCase_GetBacklogRecord_CheckResult);
	}

	TestCase_GetBacklogRecord(null,null);
	TestCase_GetBacklogRecord(false,false);

	var grBacklog = new GlideRecord('x_snc_agile_backlog');
	grBacklog.initialize();
	grBacklog.name = '_ATF_TEST';
	grBacklog.insert();

	TestCase_GetBacklogRecord(grBacklog,true);
	TestCase_GetBacklogRecord(grBacklog.sys_id,true);
	TestCase_GetBacklogRecord('_ATF_TEST',true);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
