sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetBacklogItemDependencies(vBacklogItem, aPath, vExpected)
	{
		function TestCase_GetBacklogItemDependencies_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			if (x_snc_devtools.IsObject(vResult) == false)
			{
				return false;
			}
			var aRequiredBacklogItems = x_snc_devtools.GetArrayValue(
				x_snc_devtools.GetValue(vResult,'_expected_backlog_items'));
			return (aRequiredBacklogItems.length == vExpected);
		}

		test.TestCase(
			'Function GetBacklogItemDependencies()',
			GetBacklogItemDependencies,
			{
				'Backlog Item': vBacklogItem,
				'Path': aPath,
			},
			vExpected,
			TestCase_GetBacklogItemDependencies_CheckResult);
	}

	TestCase_GetBacklogItemDependencies(null,null,false);

	var grBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItem.initialize();
	grBacklogItem.short_description = '_ATF_TEST1';
	var strBacklogItem1Sysid = grBacklogItem.insert();

	TestCase_GetBacklogItemDependencies(grBacklogItem,[],0);
	TestCase_GetBacklogItemDependencies(grBacklogItem,[],0);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
