sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetLowestPrioBacklogItem(bActive, vExpected)
	{
		function TestCase_GetLowestPrioBacklogItem_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			if (x_snc_devtools.IsValidRecord(vResult) == false)
			{
				return false;
			}
			return (vResult.order == vExpected);
		}

		test.TestCase(
			'Function GetLowestPrioBacklogItem()',
			GetLowestPrioBacklogItem,
			{
				'bActive': bActive
			},
			vExpected,
			TestCase_GetLowestPrioBacklogItem_CheckResult);
	}

	var grBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItem.initialize();
	grBacklogItem.insert();
	TestCase_GetLowestPrioBacklogItem(false,grBacklogItem.order);

	var grInactiveBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	grInactiveBacklogItem.initialize();
	grInactiveBacklogItem.state = GetBacklogItemState('complete');
	grInactiveBacklogItem.short_description = 'This is a test';
	grInactiveBacklogItem.description = 'This is a test';
	grInactiveBacklogItem.insert();
	TestCase_GetLowestPrioBacklogItem(false,grInactiveBacklogItem.order);	
	TestCase_GetLowestPrioBacklogItem(true,grBacklogItem.order);	

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
