sys_script_include.script:
function GetBacklogRecord(vBacklog)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;

	if (vBacklog == null)
	{
		var grBacklog = new GlideRecord('x_snc_agile_backlog');
		grBacklog.query();
		if (grBacklog.getRowCount() == 1)
		{
			if (grBacklog.next() == true)
			{
				return grBacklog;
			}
		}
		return false;
	}

	var grBacklog = GetRecord('x_snc_agile_backlog',vBacklog);
	if (grBacklog != false)
	{
		return grBacklog;
	}

	var grBacklog = new GlideRecord('x_snc_agile_backlog');
	grBacklog.addQuery('name','=',GetStringValue(vBacklog));
	grBacklog.query();
	if (grBacklog.getRowCount() == 1)
	{
		if (grBacklog.next() == true)
		{
			return grBacklog;
		}
	}

	return false;
}
