sys_script_include.script:
function CreateNewRequiredBacklogItem(vBacklogItem)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var IsValidSysId = x_snc_devtools.IsValidSysId;

	var grBacklogItem = GetBacklogItemRecord(vBacklogItem);
	if (IsValidRecord(grBacklogItem) == false)
	{
		return false;
	}
	var grNewBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	grNewBacklogItem.initialize();
	grNewBacklogItem.backlog = grBacklogItem.backlog;
	grNewBacklogItem.short_description = 'New backlog item required by "'+grBacklogItem.short_description+'"';
	var strNewBacklogItemSysId = grNewBacklogItem.insert();
	if (IsValidSysId(strNewBacklogItemSysId) == false)
	{
		return false;
	}
	grBacklogItem.required_backlog_items = grBacklogItem.required_backlog_items + ','+strNewBacklogItemSysId;
	grBacklogItem.update();
	return grNewBacklogItem;
}
