function GetBacklogItemStates()
{
	var states = {};
	states.draft     = 1000;
	states.ready     = 2000;
	states.dev       = 3000;
	states.done      = 4000;
	states.test      = 5000;
	states.failed    = 6000;
	states.passed    = 7000;
	states.prod      = 8000;
	states.complete  = 9000;
	states.cancelled = 10000;
	return states;
}
