sys_script_include.script:
function GetLowestPrioBacklogItem(bActive)
{
	var GetBoolValue = x_snc_devtools.GetBoolValue;
	var GetIntegerValue = x_snc_devtools.GetIntegerValue;

	var grBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	if (GetBoolValue(bActive) == true)
	{
		grBacklogItem.addActiveQuery();
	}
	grBacklogItem.setLimit(1);
	grBacklogItem.orderByDesc('order');
	grBacklogItem.query();
	if (grBacklogItem.next() == false)
	{
		return false;
	}
	return grBacklogItem;
}
