sys_script_include.script:
var AgileClientAPI = Class.create();
AgileClientAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor,
{

	ImportDataFromAttachmentsCheck: function()
	{
		var GetStringValue = x_snc_devtools.GetStringValue;
		var MakeJson = x_snc_devtools.MakeJson;

		if (gs.hasRole('x_snc_agile.agilist') == false)
		{
			return MakeJson('The user does not have the sufficient access rights');
		}		
		var strBacklogItemSysId = GetStringValue(
			this.getParameter('sysparm_backlog_item_sys_id'));

		var strText = ImportDataFromAttachmentsCheck(strBacklogItemSysId);
		return MakeJson(strText);
	},	

	start: function()
	{
		var GetStringValue = x_snc_devtools.GetStringValue;
		var IsValidRecord = x_snc_devtools.IsValidRecord;
		var StartWorker = x_snc_devtools.StartWorker;
		var StringFormat = x_snc_devtools.StringFormat;

		if (gs.hasRole('x_snc_agile.agilist') == false && gs.hasRole('admin') == false)
		{
			return '';
		}

		var strWorkerId = '';
		var strTrackerSourceTable = '';
		var strTrackerType = '';

		var strCommand = GetStringValue(
			this.getParameter("sysparm_ajax_processor_command"));
		if (strCommand == 'agile_import')
		{
			Log('AgileClientAPI::start() command is agile_import');
			var strBacklogItemSysId = GetStringValue(this.getParameter("sysparm_ajax_processor_backlog_item_sys_id"));
			if (strBacklogItemSysId == '')
			{
				LogError('AgileClientAPI.start(): ImportDataFromAttachments: no backlog_item_sys_id specified.');
				return '';
			}
			var grBacklogItem = GetBacklogItemRecord(strBacklogItemSysId);
			if (IsValidRecord(grBacklogItem) == false)
			{
				LogError('AgileClientAPI.start(): ImportDataFromAttachments: Backlog Item not found.');
				return '';
			}

			strTrackerSourceTable = 'x_snc_agile_backlog_item';
			strTrackerType = 'agile_import';

			var strProgressName =
				StringFormat(
					"Importing data to backlog item {0} - {1}",
					grBacklogItem.number,
					grBacklogItem.short_description);

			var params = {};
			params['vBacklogItem'] = strBacklogItemSysId;

			strWorkerId = StartWorker(
				strProgressName,
				'x_snc_agile.AgileWorkerAPI',
				'ImportDataFromAttachments',
				params);
		}
		else
		{
			LogError('AgileClientAPI.start(): Unknown command!');
		}


		if (strWorkerId == '')
		{
			return '';
		}

		var nCountDown = 1000;
		while (nCountDown > 0)
		{
			var grTracker = new GlideRecord('sys_execution_tracker');
			grTracker.orderByDesc('sys_created_on');
			grTracker.addQuery('source_table','=',strTrackerSourceTable);
			grTracker.addQuery('type','=',strTrackerType);
			grTracker.addQuery('state', 'IN', '0,1');
			grTracker.query();
			if (grTracker.next())
			{
				return GetStringValue(grTracker.sys_id);
			}
			nCountDown--;
		}

		return strWorkerId;
	},


    type: 'AgileClientAPI'
});
