sys_script_include.script:
function ImportDataFromAttachmentsCheck(vBacklogItem)
{
	var grBacklogItem = GetBacklogItemRecord(vBacklogItem);
	if (grBacklogItem == false)
	{
		return 'No backlog item was found.';
	}
	var grAttachment = new GlideRecord('sys_attachment');
	grAttachment.addQuery('table_name','=','x_snc_agile_backlog_item'); 
	grAttachment.addQuery('table_sys_id','=', grBacklogItem.sys_id);
	grAttachment.addQuery('file_name','CONTAINS', '.csv');
	grAttachment.query();

	var nFiles = grAttachment.getRowCount();

	if (nFiles > 0)
	{
		return '';
	}

	var strText = 'No CSV files are attached to this backlog item. Please refer to the manual on how to import backlog item and persona data from CSV files.';

	return strText;

}
