sys_script_include.script:
function HtmlRenderPageBacklogItemDependencies()
{
	var GetParam = x_snc_devtools.GetParam;
	var HtmlRenderTabNavigation = x_snc_devtools.HtmlRenderTabNavigation;
	var GetIntegerValue = x_snc_devtools.GetIntegerValue;

	var strBacklogItem = GetParam('backlogitem');

	var strMode = GetParam('mode');
	if (strMode == '')
	{
		strMode = 'topdown';
	}

	var nMaxLevel = GetIntegerValue(GetParam('maxlevel'));

	var strHtml = '';

	strHtml += '<h1>Agile - Backlog Item Dependency Viewer</h1>';

	strHtml += HtmlRenderTabNavigation(
		[
			{
				id: 'topdown',
				text: 'Project Structure Plan',
				url: '?backlogitem='+strBacklogItem+'&mode=topdown'+'&maxlevel='+nMaxLevel
			},
			{
				id: 'bottomup',
				text: 'Work Plan',
				url: '?backlogitem='+strBacklogItem+'&mode=bottomup'+'&maxlevel='+nMaxLevel
			},
			{
				id: 'map',
				text: 'Map',
				url: '?backlogitem='+strBacklogItem+'&mode=map'+'&maxlevel='+nMaxLevel
			}
		],
		strMode);


	strHtml += '<div style="padding-top: 10px;">';
		strHtml += HtmlRenderBacklogItemDependencies(strBacklogItem,strMode,nMaxLevel);
	strHtml += '</div>';

	return strHtml;
}
