sys_script_include.script:
function GetBacklogItemRecord(vBacklogItem)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	var grBacklogItem = GetRecord('x_snc_agile_backlog_item',vBacklogItem);
	if (IsValidRecord(grBacklogItem) == true)
	{
		return grBacklogItem;
	}

	var grBacklogItem = new GlideRecord('x_snc_agile_backlog_item');
	grBacklogItem.addQuery('number','=',vBacklogItem);
	grBacklogItem.query();
	if (grBacklogItem.next() == true)
	{
		return grBacklogItem;
	}
	return false;
}
