sys_ui_script.script:
function ImportDataFromAttachments(strBacklogItemSysId)
{
	var strBacklogItemSysId = GetStringValue(strBacklogItemSysId);
	var strTitle = 'Import data';

	var bUserConfirmation = false;

	StateStart();

	function StateUserConfirmation()
	{
		DoModalConfirm(
			strTitle,
			'Ready to start the data import process?'
			+'\n\n'
			+'Ready to import?',
			StateUserConfirmationUserResponse);
	}

	function StateUserConfirmationUserResponse(bResult)
	{
		if (bResult == false)
		{
			StateEnd();
			return;
		}
		bUserConfirmation = true;
		StateStart();		
	}



	function OnExecutionComplete(dialog,tracker)
	{
		var buttonViewResults = GetElementById('sysparm_button_view_results');
		if (buttonViewResults)
		{
			buttonViewResults.style.display = "none";
		}

		var buttonClose = GetElementById('sysparm_button_close');
        if (buttonClose)
		{
            buttonClose.on('click', function()
				{
					StateEnd();
				});
        }
	}

	function OnExecutionFailed(dialog)
	{
		StateError('Data import failed!');
	}

	function OnStatusUpdate(dialog)
	{
	}

	function StateAbort()
	{
		DoModalMessage(
			strTitle,
			'Aborting...',
			StateEnd);
		return;	
	}

	function StateError(strErrorMessage)
	{
		DoModalMessage(
			strTitle,
			'ERROR: '+strErrorMessage,
			StateEnd);		
		return;	
	}	

	function StateEnd()
	{
		RedirectToRecord('x_snc_agile_backlog_item',strBacklogItemSysId,'');
	}

	function StateStart()
	{
		if (bUserConfirmation == false)
		{
			StateUserConfirmation();
			return;
		}

		DoModalProgress(
			strTitle,
			'Import data',
			'x_snc_agile.AgileClientAPI',
			{
				command: 'agile_import',
				backlog_item_sys_id: strBacklogItemSysId
			},
			{
				close:
				{
					title: 'Close'
				}
			},
			{
				callbackOnExecutionComplete: OnExecutionComplete,
				callbackOnExecutionFailed: OnExecutionFailed,
				callbackOnStatusUpdate: OnStatusUpdate,
			});
	}


}
